from repeated_dna_sequences.app import findRepeatedDnaSequences


def test_case_1():
    s = "AAAAACCCCCAAAAACCCCCCAAAAAGGGTTT"
    r = ["AAAAACCCCC", "CCCCCAAAAA"]
    assert findRepeatedDnaSequences(s) == r


def test_case_2():
    s = "AAAAAAAAAAA"
    r = ["AAAAAAAAAA"]
    assert findRepeatedDnaSequences(s) == r


def test_case_3():
    s = ""
    r = ["CCTAGACAAT", "CTAGACAATT", "AAGCAGGCGC", "AGCAGGCGCT", "CCCTCTGCAA", "CTTTAATCCA", "TTTAATCCAA", "TTAATCCAAT",
         "CTGTTTGGAT", "CCAGAAAGTG", "CTCGCTTGAG", "GGATTGGAGC", "CCGAGGTATA", "GCTGTGATGA", "CGTCTCCCAA", "GTCTCCCAAG",
         "TGCTTACGAG", "CAATCGGTCT", "AATCGGTCTC", "ATCGGTCTCC"]

    assert findRepeatedDnaSequences(s) == r
